import React, { useEffect, useState } from "react";
import axios from "axios";
import "./style/spanduk.css";
import { Button } from "bootstrap";
import { CardGroup, Card } from "react-bootstrap";

export default function List() {
  const [products, setProducts] = useState([]);

  useEffect(() => {
    fetchProducts();
  });

  const fetchProducts = async () => {
    await axios.get(`http://localhost:8000/api/products`).then(({ data }) => {
      setProducts(data);
    });
  };

  const deleteProduct = async (id) => {
      const isConfirm = await Swal.fire({
          title: 'Are you sure?',
          text: "You won't be able to revert this!",
          icon: 'warning',
          showCancelButton: true,
          confirmButtonColor: '#3085d6',
          cancelButtonColor: '#d33',
          confirmButtonText: 'Yes, delete it!'
        }).then((result) => {
          return result.isConfirmed
        });

        if(!isConfirm){
          return;
        }

        await axios.delete(`http://localhost:8000/api/products/${id}`).then(({data})=>{
          Swal.fire({
              icon:"success",
              text:data.message
          })
          fetchProducts()
        }).catch(({response:{data}})=>{
          Swal.fire({
              text:data.message,
              icon:"error"
          })
        })
  }

  return (
    <div className="container">
      <div className="row">
        <div className="spanduk_nih">Hello World</div>
        {/* <div className="col-12">
                <div className="card card-body">
                    <div className="table-responsive">
                        <table className="table table-bordered mb-0 text-center">
                            <thead>
                                <tr>
                                    <th>Title</th>
                                    <th>Description</th>
                                    <th>Image</th>
                                    <th>Actions</th>
                                </tr>
                            </thead>
                            <tbody>
                                {
                                    products.length > 0 && (
                                        products.map((row, key)=>(
                                            <tr key={key}>
                                                <td>{row.title}</td>
                                                <td>{row.description}</td>
                                                <td>
                                                    <img width="50px" alt='' src={`http://localhost:8000/storage/product/image/${row.image}`} />
                                                </td>
                                                <td>
                                                    <Link to={`/product/edit/${row.id}`} className='btn btn-success me-2'>
                                                        Edit
                                                    </Link>
                                                    <Button variant="danger" onClick={()=>deleteProduct(row.id)}>
                                                        Delete
                                                    </Button>
                                                </td>
                                            </tr>
                                        ))
                                    )
                                }
                            </tbody>
                        </table>
                    </div>
                </div>
            </div> */}
        <div className="con">
          <div className="konten">
            <h2>E-SPORTS</h2>
            <hr></hr>
            <CardGroup>
              {products.length > 0 &&
                products.map((row, key) => (
                  <Card key={key}>
                    <Card.Img
                      variant="top"
                      src={`http://localhost:8000/storage/product/image/${row.image}`}
                      height="300"
                    />
                    <Card.Body>
                      <Card.Title>{row.title}</Card.Title>
                      <Card.Text>{row.description}</Card.Text>
                    </Card.Body>
                    <Button variant="primary" onClick={()=>deleteProduct(row.id)}>Read More</Button>
                  </Card>
                ))}
            </CardGroup>
          </div>
        </div>
      </div>
    </div>
  );
}
